const { expect } = require('chai')

const pusher = require('../lib/util/pusher')

describe('pusher.js', () => {
  it('should make a pusher function', () => {
    expect(pusher([])).to.be.a('function')
  })

  it('should succeed to push msg', () => {
    const testArr = []
    const pushTest = pusher(testArr)

    pushTest()
    pushTest('test')
    pushTest('test', 4, 1)

    expect(testArr).to.deep.equal(['  \n', '  test\n\n', '    test\n'])
  })
})
