const { expect } = require('chai')

const { api, formatUrl, callback } = require('../lib/trans')

const { tested, expected } = require('./mock/trans')

describe('trans', () => {
  it('should succeed to format url', () => {
    expect(formatUrl(api, tested.word, tested.urlOptions)).to.equal(expected.url)
  })

  it('should return an array includes explains after resolve', () => {
    expect(callback(tested.explains)).to.deep.equal(expected.result)
  })
})
